    /**
     * Access denied massage.
     */
    accessDenied: function(){
        Ext.Msg.show({
            title: 'Oops!',
            msg: i18n('access_denied'),
            buttons: Ext.Msg.OK,
            icon: Ext.Msg.ERROR
        });
    },

    alert: function(msg, icon){
        if(icon == 'error'){
            icon = Ext.Msg.ERROR;
        }else if(icon == 'warning'){
            icon = Ext.Msg.WARNING;
        }else if(icon == 'question'){
            icon = Ext.Msg.QUESTION;
        }else{
            icon = Ext.Msg.INFO;
        }

	    Ext.Msg.width = 1000;
	    Ext.Msg.height = 600;
	    Ext.Msg.maxHeight = 600;
	    Ext.Msg.maxWidth = 1000;
        Ext.Msg.show({
            msg: msg,
            buttons: Ext.Msg.OK,
            icon: icon
        });
	    Ext.Msg.width = 600;
	    Ext.Msg.height = 500;
	    Ext.Msg.maxWidth = 600;
	    Ext.Msg.maxHeight = 500;
    },

    resetApp:function(){
	    Ext.Msg.show({
		    title:'WAIT!!! FOR DEBUG ONLY',
		    msg: 'This will erase all patients and related data. Do you want to continue?',
		    buttons: Ext.Msg.YESNO,
		    icon: Ext.Msg.QUESTION,
		    fn:function(btn){
				if(btn == 'yes'){
					Ext.Ajax.request({
						url: 'sql/factoryReset.php',
						success: function(response){
							alert(response.responseText);
						}
					});
				}
		    }
	    });
    }

